      out.write("\t\t\t\t\t\t\t\t\t\tonClick=\"return confirm('The link will be opened in a new tab..')\" target=\"_blank\" title=\"www.awstcocalculator.com\">AWS\n");
      out.write("\t\t\t\t\t\t\t\t\t\tCalclator.</a>\n");
      out.write("\t\t\t\t\t\t\t\t</div>\n");
      out.write("\t\t\t\t\t\t</li>\n");
      out.write("\t\t\t\t\t\t\t<li class=\"flip\" id=\"flip_1_4_3\" >Use cloud computing services<span class=\"checkmark\"\n");
      out.write("\t\t\t\t\t\t\t\tname=\"1_4\"></span>\n");
      out.write("\t\t\t\t\t\t</li>\n");
      out.write("\t\t\t\t\t\t\t<div class=\"panel\" id=\"panel_1_4_3\">\n");
      out.write("\t\t\t\t\t\t\t\tSmall and Medium sized businesses can take a lot of benefit from embracing business cloud\n");
      out.write("\t\t\t\t\t\t\t\tsolutions.<br> <a\n");
